import React, {Component} from 'react';

class Filter extends Component {

    updateSearch = (event) => {
        this.setState({
            search: event.target.value.substr(0, 20)
        })
    };

    render() {
        return (
            <div>
                <input type="text"
                       placeholder="Search"
                       value={this.state.search}
                       onChange={this.updateSearch.bind(this)}
                />
            </div>
        );
    }
}

export default Filter;
