onDragStart = (ev, name) => {
    console.log('dragstart:',name);
    ev.dataTransfer.setData("id", name);
}


onDragOver = (ev) => {
    ev.preventDefault();
}

onDrop = (ev, colNum) => {
    let id = ev.dataTransfer.getData("id");
    alert(ev.dataTransfer.getData("id"));
    let contacts = this.state.contacts.filter((contact) => {
        if (contact.name == id) {
            contact.col = colNum;
        }
        return contact;
    });

    this.setState({
        ...this.state,
        contacts
    });
}
