import React, {Component} from 'react';

class Toggle extends Component {
    render() {
        state = {
            on: false,
        };

        toggle = () => {
            this.setState({
                on: !this.state.on
            })
        };

        return (
            <div>
                {this.state.on && (<h1>Toggle Me</h1>)}
                <button onClick={this.toggle}>Show/Hide</button>
            </div>
        );
    }
}

export default Toggle;
