import React, {Component} from 'react';

class Toggle extends Component {
    state = {
        toggle: true,
        value: 15
    }

    toggle = () => {
        this.setState({
            toggle: !this.state.toggle
        })
    }


    render() {
        return (
            <div>
                {this.state.toggle &&
                <p>This should show and hide</p>
                }
                <button onClick={this.toggle}>Show / Hide</button>

                {this.state.value}
            </div>
        );
    }
}

export default Toggle;
